import React from "react";
import Helmet from "../components/Helmet/Helmet";
import { Container, Row, Col } from "reactstrap";
import { Link } from "react-router-dom";
import guyImg from "../assets/images/delivery-guy.png";
import "../styles/hero-section.css";

const Home = () => {
  return (
    <Helmet title="Home">
      <section className="hero-section">
        <Container>
          <Row className="align-items-center">
            <Col lg="6" md="12" className="order-md-2">
              <div className="hero__content">
                <h5 className="hero__subtitle mb-3">Easy Order & Fast Delivery</h5>
                <h1 className="hero__title mb-4">
                  <span>Enjoy</span> Your Favorite Pizza
                </h1>

                <button className="order__btn">
                  <Link to="/pizzas" className="btn-link">
                    Menu <i className="ri-arrow-right-s-line"></i>
                  </Link>
                </button>
              </div>
            </Col>

            <Col lg="6" md="12" className="order-md-1">
              <div className="hero__img">
                <img src={guyImg} alt="Delivery Guy" className="w-100" />
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Home;
